// app-routing.module.ts
import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { DashboardComponent } from './features/dashboard/dashboard/dashboard.component';
import {LandingComponent} from "./features/landing/landing.component";

const routes: Routes = [
  // Rota inicial para landing
  { path: '', component: LandingComponent },

  // Rota do Dashboard
  {
    path: 'dashboard',
    loadChildren: () => import('./features/dashboard/dashboard/dashboard.module')
      .then(m => m.DashboardModule)
  },

  // Lazy loading para o módulo Analytics
  {
    path: 'analytics',
    loadChildren: () => import('./features/analytics/analytics.module')
      .then(m => m.AnalyticsModule)
  },

  // Lazy loading para o módulo Social Media
  {
    path: 'social-media',
    loadChildren: () => import('./features/social-media/social-media.module')
      .then(m => m.SocialMediaModule)
  },

  // Rota wildcard - redireciona para landing se a rota não existir
  { path: '**', redirectTo: '' }
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }
